var drawPieChart=function(f,a){var b=document.getElementById("pie");var k=b.getContext("2d");var j=b.width/2;y=b.height/2;var d,g,c,h=getTotal(f);for(var e=0;e<f.length;e++){d=a[e];g=calculateStart(f,e,h);c=calculateEnd(f,e,h);k.beginPath();k.fillStyle=d;k.moveTo(j,y);k.arc(j,y,y-100,g,c);k.fill();k.rect(b.width-200,y-e*30,12,12);k.fill();k.font="13px sans-serif";k.fillText(f[e].label+" - "+f[e].value+" ("+calculatePercent(f[e].value,h)+"%)",b.width-200+20,y-e*30+10)}};var calculatePercent=function(b,a){return(b/a*100).toFixed(2)};var getTotal=function(c){var b=0;for(var a=0;a<c.length;a++){b+=c[a].value}return b};var calculateStart=function(c,a,b){if(a===0){return 0}return calculateEnd(c,a-1,b)};var calculateEndAngle=function(c,a,b){var e=c[a].value/b*360;var d=(a===0)?0:calculateEndAngle(c,a-1,b);return(e+d)};var calculateEnd=function(c,a,b){return degreeToRadians(calculateEndAngle(c,a,b))};var degreeToRadians=function(a){return a*Math.PI/180};var data=[{label:"Food",value:90},{label:"Party",value:150},{label:"Rent",value:80},{label:"Chocolates",value:120}];var colors=["#39CCCC","#3D9970","#001F3F","#85144B"];drawPieChart(data,colors);